/**
 * @author Nicolas SQUEREN <nicosqueren@gmail.fr>
 * @copyright Copyright (c) 2020 Moulinroty (https://www.moulinroty.com)
 * @link https://www.moulinroty.com
 */
import React, {Component} from "react";

import Draggable from "react-draggable";
// eslint-disable-next-line no-unused-vars
import { DeleteNote } from "../service/Api"

import EditModal from "../container/EditModal";

class Note extends Component{
	constructor() {
		super();
		this.state = {
			openModal:false
		}
	}
	getRandomColor(){
		var letters = '0123456789ABCDEF';
		var color = '#';
		for (var i = 0; i < 6; i++) {
			color += letters[Math.floor(Math.random() * 16)];
		}
		return color;
	}
	handledeleteNote = (notId) => {
		DeleteNote(notId)
	}
	handlemodalNote = (note) => {
		console.log(note)
		this.setState({openModal: true})

	}
	render() {
		const { note } = this.props
		return (
			note
			? (
					<Draggable>
						<div className="drag" style={{backgroundColor:this.getRandomColor()}}>
							<h2 className="card">
								{note.title}
							</h2>
							<div className="desc">
								{note.description}
							</div>
							<button onClick={() => this.handledeleteNote(note.id)}>Delete note</button>
							<button onClick={() => this.handlemodalNote(note)}>Update note</button>
							<EditModal updateMode isOpen={this.state.openModal} note={note}/>
						</div>

					</ Draggable>

				)
			: 'No DATA'
		)
	}
}

export default Note
