/**
 * @author Nicolas SQUEREN <nicosqueren@gmail.fr>
 * @copyright Copyright (c) 2020 Moulinroty (https://www.moulinroty.com)
 * @link https://www.moulinroty.com
 */
import React, {Component} from "react";
import ReactModal from 'react-modal';
import  {Button}  from '@material-ui/core';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';


class EditModal extends Component{
	constructor(props) {
		super(props);
		this.state = {
			title: '',
			description: '',
			isOpen: false
		}
	}
	handleChange = (event) => {
		this.setState({
			[event.target.name]: event.target.value
		})
	}
	handleOpenModal = () => {
		this.setState({isOpen: true})
	}
	handleCloseModal = () => {
		this.setState({isOpen: false})
	}
	handleSubmit = (event) => {
		event.preventDefault()
		const { title, description } = this.state
		if (title && description){
			var body = {
				title : title,
				description: description
			}
			var params = { method: 'POST',
				mode: 'cors',
				cache: 'default' ,
				headers: {
				'Content-Type': 'application/json; charset=UTF-8'
				},
				body: JSON.stringify(body)
			};
			fetch('https://api.myidea.fr/v1/notes', params)
				.then(response => console.log(response))
				.catch(error => console.log(error))
		}
	}
	render() {
		const { title, description, isOpen } = this.state
		return (
			<div>
				<Button className='action_btn' onClick={this.handleOpenModal}>CREATE NOTE</Button>
				<ReactModal
				isOpen={isOpen}
				>
					<div>
						<h1>EDIT NOTE</h1>

						<form className='block_form' method='post' onSubmit={this.handleSubmit}>
							<FormControl className='input_form'>
								<InputLabel>Titre :</InputLabel>
								<Input  name='title' onChange={this.handleChange} value={title}/>
							</FormControl>
							<FormControl>
								<InputLabel>Description :</InputLabel>
								<Input  onChange={this.handleChange} name='description' value={description}/>
							</FormControl>

							<Button className="submit_btn" type="submit">Submit</Button>
						</form>
						<div className='closemodal' onClick={this.handleCloseModal}>Close modal</div>
					</div>
				</ReactModal>
			</div>
		)
	}
}

export default EditModal
