/**
 * @author Nicolas SQUEREN <nicosqueren@gmail.fr>
 * @copyright Copyright (c) 2020 Moulinroty (https://www.moulinroty.com)
 * @link https://www.moulinroty.com
 */
import React, {Component} from "react";
import ReactModal from 'react-modal';
import  {Button}  from '@material-ui/core';
class EditModal extends Component{
	constructor(props) {
		super(props);
		this.state = {
			title: '',
			description: '',
			isOpen: false
		}
	}
	handleChange = (event) => {
		this.setState({
			[event.target.name]: event.target.value
		})
	}
	handleOpenModal = () => {
		this.setState({isOpen: true})
	}
	handleCloseModal = () => {
		this.setState({isOpen: false})
	}
	handleSubmit = (event) => {
		event.preventDefault()
		const { title, description } = this.state
		if (title && description){
			var myHeaders = new Headers();
			var body = {
				title : title,
				description: description
			}
			var params = { method: 'POST',
				headers: myHeaders,
				mode: 'cors',
				cache: 'default' ,
				body: JSON.stringify(body)
			};
			fetch('https://api.myidea.fr/v1/notes', params)
				.then(response => console.log(response))
				.catch(error => console.log(error))
		}
	}
	render() {
		const { title, description, isOpen } = this.state
		return (
			<div>
				<Button color="primary" onClick={this.handleOpenModal}>OPEN MODAL</Button>
				<ReactModal
				isOpen={isOpen}
				>
					<div>
						<h1>EDIT NOTE</h1>

						<form onSubmit={this.handleSubmit}>
							<label>Titre :</label>
							<input name='title' onChange={this.handleChange} value={title}/>
							<label>Description :</label>
							<input  onChange={this.handleChange} name='description' value={description}/>
							<button type="submit">Submit</button>
						</form>
						<div className='closemodal' onClick={this.handleCloseModal}>Close modal</div>
					</div>
				</ReactModal>
			</div>
		)
	}
}

export default EditModal
