/**
 * @author Nicolas SQUEREN <nicosqueren@gmail.fr>
 * @copyright Copyright (c) 2020 Moulinroty (https://www.moulinroty.com)
 * @link https://www.moulinroty.com
 */
import React, {Component} from "react";
import ReactModal from 'react-modal';
import  {Button}  from '@material-ui/core';
import Input from '@material-ui/core/Input';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';
import TextField from '@material-ui/core/TextField';
import { createNote } from '../service/Api';


class EditModal extends Component{
	constructor(props) {
		super(props);
		this.state = {
			title: '',
			description: '',
			isOpen: false
		}
	}
	handleChange = (event) => {
		this.setState({
			[event.target.name]: event.target.value
		})
	}
	handleOpenModal = () => {
		this.setState({isOpen: true})
	}
	handleCloseModal = () => {
		this.setState({isOpen: false})
	}
	handleSubmit = (event) => {
		event.preventDefault()
		createNote(this.state)
			.then( () => this.setState({isOpen: false}), console.log('La note à été ajoutée') )
	}
	render() {
		const { title, description, isOpen } = this.state
		return (
			<div>
				<Button className='action_btn' onClick={this.handleOpenModal}>CREATE NOTE</Button>
				<ReactModal
				isOpen={isOpen}
				>
					<div className='modal_container'>
						<h1>EDIT NOTE</h1>

						<form className='block_form' method='post' onSubmit={this.handleSubmit}>
							<FormControl className='input_form'>
								{/*<InputLabel>Titre :</InputLabel>*/}
								{/*<Input  name='title' />*/}
								<TextField
									id="filled-full-width"
									label="Titre"
									style={{ margin: 8 }}
									placeholder="Votre titre"
									helperText="ce sera le titre de votre note"
									fullWidth
									margin="normal"
									InputLabelProps={{
										shrink: true,
									}}
									variant="filled"
									onChange={this.handleChange} value={title}
								/>
							</FormControl >
							<FormControl className='input_form'>
								<InputLabel>Description :</InputLabel>
								<Input  onChange={this.handleChange} name='description' value={description}/>
							</FormControl>

							<Button className="submit_btn" type="submit">Submit</Button>
						</form>
						<div className='closemodal' onClick={this.handleCloseModal}>Close modal</div>
					</div>
				</ReactModal>
			</div>
		)
	}
}

export default EditModal
