/**
 * @author Nicolas SQUEREN <nicosqueren@gmail.fr>
 * @copyright Copyright (c) 2020 Moulinroty (https://www.moulinroty.com)
 * @link https://www.moulinroty.com
 */
import React, {Component} from "react";
import { updateNote } from "../service/Api"
import ReactModal from "react-modal";
class Note extends Component{
	constructor(props) {
		super(props);
		this.state = {
			title: '',
			description: '',
			isOpen: this.props.open
		}
	}
	handleupdateNote = (notId) => {
		updateNote(notId)
	}
	handleCloseModal = () => {
		this.setState({isOpen: false})
	}
	render() {
		const { note } = this.props
		const { isOpen } = this.state
		return (
			note
				? (
					<ReactModal
						isOpen={isOpen}
					>
						<div className="drag" style={{backgroundColor:this.getRandomColor()}}>
							<h2 className="card">
								{note.title}
							</h2>
							<div className="desc">
								{note.description}
							</div>
							<button onClick={() => this.handleupdateNote(note.id)}>Update note</button>
						</div>

					</ReactModal>

				)
				: 'No DATA'
		)
	}
}

export default Note
