}
export function getNotes () {
	return new Promise((resolve, reject) => {
		window.fetch(URL + 'notes')
			.then((response) => response.json())
			.then((result) => {
				console.log('Réponse API getNotes', result)
				resolve(result)
			})
			.catch((error) => {
				console.error(error)
				reject(error)
			})
	})
}

export function createNote(note){
	return new Promise((resolve, reject) => {
		if (note.title && note.description){
			var body = {
				title : note.title,
				description: note.description
			}
			var params = { method: 'POST',
				mode: 'cors',
				cache: 'default' ,
				headers: {
					'Content-Type': 'application/json; charset=UTF-8'
				},
				body: JSON.stringify(body)
			};
			fetch('https://api.myidea.fr/v1/notes', params)
				.then(response => {
					console.log(response)
					resolve()
				})
				.catch(error => reject(error))
		}else{
			reject('Informations manquantes')
		}
	})
}

export function DeleteNote (id){
	const params = {
		method: 'DELETE',

	};
	return new Promise((resolve, reject) => {
			fetch(URL + "notes/" + id, params)
				.then(response => {
					console.log(response)
					resolve()
					setTimeout(window.location.reload())
				})
				.catch(error => reject(error))
	})
}

export function updateNote (id){
	const requestOptions = {
		method: 'PATCH'
	};
	return new Promise((resolve, reject) => {
		fetch(URL + "notes/" + id, requestOptions)
			.then(response => {
				console.log(response)
				resolve()
			})
			.catch(error => reject(error))
	})
}
